import axios from 'axios';
import { 
  User, Hotel, Room, Booking, Payment,
  CreateUserData, CreateHotelData, CreateRoomData, CreateBookingData, CreatePaymentData,
  UpdateUserData, UpdateHotelData, UpdateRoomData, UpdateBookingData, UpdatePaymentData
} from '../types';

// Cấu hình base URL
const BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/v1';

// Tạo axios instance
const api = axios.create({
  baseURL: BASE_URL,
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor để thêm token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor để xử lý lỗi
api.interceptors.response.use(
  (response) => {
    // Trả về data từ response.data.data nếu có, nếu không thì response.data
    if (response.data && response.data.data !== undefined) {
      return { ...response, data: response.data.data };
    }
    return response;
  },
  (error) => {
    if (error.response?.status === 401) {
      // Token hết hạn, chuyển về trang đăng nhập
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// ========== AUTH API ==========
export const authAPI = {
  // Đăng ký
  register: async (userData: CreateUserData) => {
    const response = await api.post('/users/register', userData);
    return response.data;
  },

  // Đăng nhập
  login: async (credentials: { username: string; password: string }) => {
    const response = await api.post('/users/login', credentials);
    const { access_token, user } = response.data;

    // Lưu token và user info
    localStorage.setItem('token', access_token);
    localStorage.setItem('user', JSON.stringify(user));

    return { token: access_token, user };
  },

  // Đăng xuất
  logout: () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
  },

  // Lấy thông tin user hiện tại
  getCurrentUser: async (): Promise<User> => {
    const response = await api.get('/users/me');
    return response.data;
  },

  // Thay đổi mật khẩu
  changePassword: async (userId: number, passwords: { old_password: string; new_password: string }) => {
    const response = await api.post(`/users/${userId}/change-password`, passwords);
    return response.data;
  },
};

// ========== USERS API ==========
export const usersAPI = {
  // Lấy danh sách users (admin only)
  getUsers: async (params?: { skip?: number; limit?: number; active_only?: boolean }): Promise<User[]> => {
    const response = await api.get('/users', { params });
    return response.data;
  },

  // Lấy user theo ID
  getUser: async (userId: number): Promise<User> => {
    const response = await api.get(`/users/${userId}`);
    return response.data;
  },

  // Cập nhật user
  updateUser: async (userId: number, userData: UpdateUserData): Promise<User> => {
    const response = await api.put(`/users/${userId}`, userData);
    return response.data;
  },

  // Xóa user (admin only)
  deleteUser: async (userId: number) => {
    const response = await api.delete(`/users/${userId}`);
    return response.data;
  },

  // Thống kê users (admin only)
  getUserStats: async () => {
    const response = await api.get('/users/stats/overview');
    return response.data;
  },
};

// ========== HOTELS API ==========
export const hotelsAPI = {
  // Lấy danh sách khách sạn
  getHotels: async (params?: {
    skip?: number;
    limit?: number;
    city?: string;
    country?: string;
    min_rating?: number;
    search?: string;
  }): Promise<Hotel[]> => {
