import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { hotelsAPI, roomsAPI } from '../services/api';
import { Hotel, Room } from '../types';

const HomePage: React.FC = () => {
  const navigate = useNavigate();
  const [hotels, setHotels] = useState<any[]>([]);
  const [featuredRooms, setFeaturedRooms] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [bookingForm, setBookingForm] = useState({
    destination: '',
    checkIn: '',
    checkOut: '',
    guests: 1
  });

  // Demo data vá»›i áº£nh thá»±c táº¿
  const demoRooms = [
    {
      id: 1,
      room_number: "101",
      room_type: "single",
      price_per_night: 500000,
      capacity: 1,
      description: "PhÃ²ng Ä‘Æ¡n sang trá»ng vá»›i view thÃ nh phá»‘",
      amenities: ["WiFi miá»…n phÃ­", "Äiá»u hÃ²a", "TV mÃ n hÃ¬nh pháº³ng", "Minibar"],
      images: [
        "https://images.unsplash.com/photo-1631049307264-da0ec9d70304?w=800&h=600&fit=crop",
        "https://images.unsplash.com/photo-1595576508898-0ad5c879a061?w=800&h=600&fit=crop"
      ],
      is_available: true,
      hotel: { name: "Luxury Hotel Saigon", star_rating: 5 }
    },
    {
      id: 2,
      room_number: "201",
      room_type: "double",
      price_per_night: 800000,
      capacity: 2,
      description: "PhÃ²ng Ä‘Ã´i cao cáº¥p vá»›i giÆ°á»ng king-size",
      amenities: ["WiFi miá»…n phÃ­", "Äiá»u hÃ²a", "TV 55 inch", "Bá»“n táº¯m jacuzzi"],
      images: [
        "https://images.unsplash.com/photo-1582719478250-c89cae4dc85b?w=800&h=600&fit=crop",
        "https://images.unsplash.com/photo-1566665797739-1674de7a421a?w=800&h=600&fit=crop"
      ],
      is_available: true,
      hotel: { name: "Luxury Hotel Saigon", star_rating: 5 }
    },
    {
      id: 3,
      room_number: "301",
      room_type: "suite",
      price_per_night: 1500000,
