import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { hotelsAPI, roomsAPI, utilsAPI } from '../services/api';
import { Hotel, Room } from '../types';

const HomePage: React.FC = () => {
  const navigate = useNavigate();
  const [hotels, setHotels] = useState<Hotel[]>([]);
  const [featuredRooms, setFeaturedRooms] = useState<Room[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchCity, setSearchCity] = useState('');
  const [checkInDate, setCheckInDate] = useState('');
  const [checkOutDate, setCheckOutDate] = useState('');
  const [guestCount, setGuestCount] = useState(1);

  useEffect(() => {
    loadInitialData();
  }, []);

  const loadInitialData = async () => {
    try {
      setLoading(true);

      // Lấy danh sách khách sạn nổi bật
      const hotelsData = await hotelsAPI.getHotels({ limit: 6, min_rating: 4 });
      setHotels(hotelsData);

      // Lấy danh sách phòng nổi bật
      const roomsData = await roomsAPI.getRooms({ limit: 8, available_only: true });
      setFeaturedRooms(roomsData);

    } catch (error) {
      console.error('Lỗi tải dữ liệu:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    try {
      if (!checkInDate || !checkOutDate) {
        alert('Vui lòng chọn ngày check-in và check-out');
        return;
      }

      const searchParams = {
        available_only: true,
        check_in_date: checkInDate,
        check_out_date: checkOutDate,
        capacity: guestCount,
        ...(searchCity && { hotel_id: undefined }), // Tìm theo thành phố sẽ implement sau
      };

      navigate('/rooms', { state: { searchParams, searchQuery, searchCity } });
    } catch (error) {
      console.error('Lỗi tìm kiếm:', error);
      alert('Có lỗi xảy ra khi tìm kiếm. Vui lòng thử lại.');
    }
  };

  const getTodayDate = () => {
    return new Date().toISOString().split('T')[0];
  };

  const getTomorrowDate = () => {
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    return tomorrow.toISOString().split('T')[0];
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Đang tải dữ liệu...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section với Form Tìm Kiếm */}
      <section className="relative bg-gradient-to-r from-blue-600 to-blue-800 text-white">
        <div className="absolute inset-0 bg-black bg-opacity-30"></div>
        <div className="relative container mx-auto px-4 py-20">
          <div className="text-center mb-12">
            <h1 className="text-5xl font-bold mb-4">
              🏨 Hệ Thống Đặt Phòng Khách Sạn
            </h1>
            <p className="text-xl mb-8">
              Tìm và đặt phòng khách sạn tốt nhất với giá ưu đãi
            </p>
          </div>

          {/* Form Tìm Kiếm */}
          <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-lg p-6">
            <div className="grid md:grid-cols-5 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Địa điểm
                </label>
                <input
                  type="text"
                  value={searchCity}
                  onChange={(e) => setSearchCity(e.target.value)}
                  placeholder="Thành phố, khách sạn..."
                  className="w-full px-3 py-2 border border-gray-300 rounded-md text-gray-900 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Ngày nhận phòng
                </label>
                <input
                  type="date"
                  value={checkInDate}
                  onChange={(e) => setCheckInDate(e.target.value)}
                  min={getTodayDate()}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md text-gray-900 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Ngày trả phòng
                </label>
                <input
                  type="date"
                  value={checkOutDate}
                  onChange={(e) => setCheckOutDate(e.target.value)}
                  min={checkInDate || getTomorrowDate()}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md text-gray-900 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Số khách
                </label>
                <select
                  value={guestCount}
                  onChange={(e) => setGuestCount(Number(e.target.value))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md text-gray-900 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                >
                  {[1, 2, 3, 4, 5, 6].map(num => (
                    <option key={num} value={num}>{num} khách</option>
                  ))}
                </select>
              </div>

              <div className="flex items-end">
                <button
                  onClick={handleSearch}
                  className="w-full bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-4 rounded-md transition duration-200"
                >
                  🔍 Tìm Phòng
                </button>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Khách Sạn Nổi Bật */}
      <section className="py-16">
        <div className="container mx-auto px-4">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">
              🏨 Khách Sạn Nổi Bật
            </h2>
            <p className="text-gray-600">
              Các khách sạn được đánh giá cao nhất từ khách hàng
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {hotels.map((hotel) => (
              <div key={hotel.id} className="bg-white rounded-lg shadow-lg overflow-hidden hover:shadow-xl transition duration-300">
                <div className="h-48 bg-gradient-to-r from-blue-400 to-blue-600 flex items-center justify-center">
                  <span className="text-white text-4xl">🏨</span>
                </div>
                <div className="p-6">
                  <h3 className="text-xl font-semibold mb-2">{hotel.name}</h3>
                  <p className="text-gray-600 mb-2">
                    📍 {hotel.city}, {hotel.country}
                  </p>
                  <div className="flex items-center mb-3">
                    <span className="text-yellow-400">
                      {'⭐'.repeat(hotel.star_rating)}
                    </span>
                    <span className="ml-2 text-sm text-gray-600">
                      ({hotel.star_rating} sao)
                    </span>
                  </div>
                  <p className="text-gray-700 text-sm mb-4 line-clamp-2">
                    {hotel.description}
                  </p>
                  <div className="flex justify-between items-center">
                    <Link
                      to={`/hotels/${hotel.id}`}
                      className="text-blue-600 hover:text-blue-800 font-medium"
                    >
                      Xem chi tiết →
                    </Link>
                    <Link
                      to={`/rooms?hotel_id=${hotel.id}`}
                      className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md text-sm transition duration-200"
                    >
                      Đặt phòng
                    </Link>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className="text-center mt-8">
            <Link
              to="/hotels"
              className="inline-block bg-blue-600 hover:bg-blue-700 text-white font-medium py-3 px-6 rounded-md transition duration-200"
            >
              Xem tất cả khách sạn
            </Link>
          </div>
        </div>
      </section>

      {/* Phòng Nổi Bật */}
      <section className="py-16 bg-gray-100">
        <div className="container mx-auto px-4">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">
              🛏️ Phòng Nổi Bật
            </h2>
            <p className="text-gray-600">
              Những phòng đẹp nhất với tiện nghi cao cấp
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
            {featuredRooms.map((room) => (
              <div key={room.id} className="bg-white rounded-lg shadow-lg overflow-hidden hover:shadow-xl transition duration-300">
                <div className="h-40 bg-gradient-to-r from-green-400 to-green-600 flex items-center justify-center">
                  <span className="text-white text-3xl">🛏️</span>
                </div>
                <div className="p-4">
                  <h4 className="text-lg font-semibold mb-2">
                    Phòng {room.room_number}
                  </h4>
                  <p className="text-gray-600 text-sm mb-2">
                    {room.room_type} • {room.capacity} khách
                  </p>
                  <p className="text-blue-600 font-bold text-lg mb-3">
                    {utilsAPI.formatCurrency(room.price_per_night)}/đêm
                  </p>
                  <p className="text-gray-700 text-sm mb-3 line-clamp-2">
                    {room.description}
                  </p>
                  <Link
                    to={`/rooms/${room.id}`}
                    className="block text-center bg-green-600 hover:bg-green-700 text-white py-2 rounded-md text-sm transition duration-200"
                  >
                    Đặt ngay
                  </Link>
                </div>
              </div>
            ))}
          </div>

          <div className="text-center mt-8">
            <Link
              to="/rooms"
              className="inline-block bg-green-600 hover:bg-green-700 text-white font-medium py-3 px-6 rounded-md transition duration-200"
            >
              Xem tất cả phòng
            </Link>
          </div>
        </div>
      </section>

      {/* Dịch Vụ Nổi Bật */}
      <section className="py-16">
        <div className="container mx-auto px-4">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">
              ✨ Tại Sao Chọn Chúng Tôi?
            </h2>
            <p className="text-gray-600">
              Những lý do khiến khách hàng tin tựởng và lựa chọn dịch vụ của chúng tôi
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
            <div className="text-center">
              <div className="bg-blue-100 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-3xl">💰</span>
              </div>
              <h3 className="text-xl font-semibold mb-2">Giá Tốt Nhất</h3>
              <p className="text-gray-600">
                Cam kết giá tốt nhất thị trường với nhiều ưu đãi hấp dẫn
              </p>
            </div>

            <div className="text-center">
              <div className="bg-green-100 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-3xl">🏆</span>
              </div>
              <h3 className="text-xl font-semibold mb-2">Chất Lượng Cao</h3>
              <p className="text-gray-600">
                Chỉ hợp tác với những khách sạn có chất lượng dịch vụ tốt nhất
              </p>
            </div>

            <div className="text-center">
              <div className="bg-yellow-100 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-3xl">⚡</span>
              </div>
              <h3 className="text-xl font-semibold mb-2">Đặt Phòng Nhanh</h3>
              <p className="text-gray-600">
                Quy trình đặt phòng đơn giản, nhanh chóng chỉ trong vài phút
              </p>
            </div>

            <div className="text-center">
              <div className="bg-purple-100 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-3xl">🛡️</span>
              </div>
              <h3 className="text-xl font-semibold mb-2">An Toàn Bảo Mật</h3>
              <p className="text-gray-600">
                Thông tin thanh toán được bảo mật tuyệt đối với công nghệ hiện đại
              </p>
            </div>
          </div>
        </div>
      </section>

      {/* Call to Action */}
      <section className="py-16 bg-blue-600 text-white">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-3xl font-bold mb-4">
            Sẵn Sàng Cho Chuyến Đi Của Bạn?
          </h2>
          <p className="text-xl mb-8">
            Đặt phòng ngay hôm nay và nhận ưu đãi đặc biệt!
          </p>
          <div className="space-x-4">
            <Link
              to="/rooms"
              className="inline-block bg-white text-blue-600 font-medium py-3 px-8 rounded-md hover:bg-gray-100 transition duration-200"
            >
              🔍 Tìm Phòng
            </Link>
            <Link
              to="/register"
              className="inline-block border-2 border-white text-white font-medium py-3 px-8 rounded-md hover:bg-white hover:text-blue-600 transition duration-200"
            >
              📝 Đăng Ký Ngay
            </Link>
          </div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
